function ModalWorkflow(opts){var self={};var responseCallbacks=opts.responses||{};$("body > .modal").remove();var container=$('<div class="modal fade" tabindex="-1" role="dialog" aria-hidden="true">\n    <div class="modal-dialog">\n        <div class="modal-content">\n            <button type="button" class="button close icon text-replace icon-cross" data-dismiss="modal" aria-hidden="true">&times;</button>\n            <div class="modal-body"></div>\n        </div>\x3c!-- /.modal-content --\x3e\n    </div>\x3c!-- /.modal-dialog --\x3e\n</div>');$("body").append(container);container.modal("hide");self.body=container.find(".modal-body");self.loadUrl=function(url,urlParams){$.get(url,urlParams,self.loadResponseText,"text")};self.postForm=function(url,formData){$.post(url,formData,self.loadResponseText,"text")};self.ajaxifyForm=function(formSelector){$(formSelector).each(function(){var action=this.action;if(this.method.toLowerCase()=="get"){$(this).submit(function(){self.loadUrl(action,$(this).serialize());return false})}else{$(this).submit(function(){self.postForm(action,$(this).serialize());return false})}})};self.loadResponseText=function(responseText){var response=eval("("+responseText+")");self.loadBody(response)};self.loadBody=function(response){if(response.html){self.body.html(response.html);container.modal("show")}if(response.onload){response.onload(self)}};self.respond=function(responseType){if(responseType in responseCallbacks){args=Array.prototype.slice.call(arguments,1);responseCallbacks[responseType].apply(self,args)}};self.close=function(){container.modal("hide")};self.loadUrl(opts.url,opts.urlParams);return self}